import React, { Component } from 'react';
import '../pages/HomePage/homePage.less';

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showPopView: false,
    };
  }

  mouseEnter = (e) => {
    e.persist();
    if (e.target.className === '') {
      e.target.className = 'position-li';
    }
    let currentTag=document.getElementById(e.target.id);
    currentTag.innerHTML=JSON.stringify(this.popView());
    this.setState({
      showPopView: true
    })
  }
  // 弹窗
  popView = () => {
    return (
      <div className="slecttion" onMouseOver={this.mouseEnter}>
        <p>中国大陆</p>
        <p>香港</p>
        <p>美国</p>
        <p>欧洲</p>
        <p>法国</p>
      </div>
    );
  }


  mouseOut = (e) => {
    e.target.className = "";
    this.setState({
      showPopView: false
    })
  }

  render() {
    let { showPopView } = this.state;
    return (
      <React.Fragment>
        <div className="header">
          <div className="header-container">
            <ul>
              <li className="" value="中国大陆" onMouseOver={this.mouseEnter} onMouseOut={this.mouseOut} id="address" ref={(ref) => this.address = ref}>中国大陆
              {showPopView ? this.popView() : null}
              </li>
              <li id="login">请登录</li>
              <li id="mobileTaobao">手机逛淘宝</li>
            </ul>
            <ul>
              <li className="" id="myTaobao" onMouseOver={this.mouseEnter} onMouseOut={this.mouseOut}>我的淘宝
              </li>
              <li className="" id="shopCar" onMouseOver={this.mouseEnter} onMouseOut={this.mouseOut}>购物车
              </li>
              <li className="" id="bookMark" onMouseOver={this.mouseEnter} onMouseOut={this.mouseOut}>收藏夹
              </li>
              <li className="" id="goodsCategory">商品分类</li>
              <li className="" id="qianNiu" onMouseOver={this.mouseEnter} onMouseOut={this.mouseOut}>千牛卖家中心
              </li>
              <li className="" id="cantactServer" onMouseOver={this.mouseEnter} onMouseOut={this.mouseOut}>联系客服
              </li>
              <li className="" id="navigation" onMouseOver={this.mouseEnter} onMouseOut={this.mouseOut}>网站导航
              </li>
            </ul>
          </div>
        </div>
      </React.Fragment>
    )
  }
}

export default Header;
